package variable;

public class DoubleTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double dNum=3.14;
		
		//이런경우 더블로 자동형변환을 통해서 더블로 저장됨 해결방법 뒤에 F 붙이기
		/* float fNum=3.14; */
		
		float fNum=3.14F;
		System.out.println(dNum);
		System.out.println(fNum);
	}

}
